cat input.14 | sed 's/[^0-9 ]//g' | awk 'd=0; b=$2; r=$3; {for(t=1; t <= 2503; t++) { if(b) { b--; d+=$1 } else if(r > 1) { r-- } else { b=$2; r=$3 }; print d }}' | grep -v ' ' | awk '{ d[int((NR-1)/2503)][NR%2503] = $1 } END {for(t=0;t<2503;t++) { best = d[0][t]; for(r=1;r<9;r++) {if(d[r][t] > best) {best=d[r][t]}}; for(r=0;r<9;r++) {if(d[r][t] == best) print r}}}' | sort | uniq -c
